import React, { useState } from "react";
import { connect } from "react-redux";
import {
  getUserProfiles,
  getUserProfile,
  updateUserProfile,
  deleteUserProfile,
} from "../Redux/Reducers/ProfileSlice";

const UserProfilePage = ({
  userProfiles,
  userProfile,
  loading,
  getUserProfiles,
  getUserProfile,
  updateUserProfile,
  deleteUserProfile,
}) => {
  const [searchUsername, setSearchUsername] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    // Add more fields as needed
  });

  const { name, email } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSearch = () => {
    if (searchUsername.trim() !== "") {
      getUserProfiles(searchUsername);
    }
    getUserProfiles(searchUsername);
  };

  const handleViewProfile = (userId) => {
    getUserProfile(userId);
  };

  const handleUpdateProfile = () => {
    updateUserProfile(formData);
  };

  const handleDeleteProfile = () => {
    deleteUserProfile();
  };

  return (
    <div>
      <h1>User Profiles</h1>
      <div>
        <input
          type="text"
          placeholder="Search by username"
          value={searchUsername}
          onChange={(e) => setSearchUsername(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <ul>
        {userProfiles.map((profile) => (
          <li key={profile._id}>
            <p>{profile.name}</p>
            <button onClick={() => handleViewProfile(profile._id)}>View Profile</button>
          </li>
        ))}
      </ul>

      {userProfile && (
        <div>
          <h2>View Profile</h2>
          <form>
            <div>
              <label>Name:</label>
              <input type="text" name="name" value={name} onChange={handleChange} />
            </div>
            <div>
              <label>Email:</label>
              <input type="email" name="email" value={email} onChange={handleChange} />
            </div>
            {/* Add more input fields for other profile data */}
          </form>
          <button onClick={handleUpdateProfile}>Update Profile</button>
          <button onClick={handleDeleteProfile}>Delete Profile</button>
        </div>
      )}

      {loading && <p>Loading user profiles...</p>}
    </div>
  );
};

const mapStateToProps = (state) => ({
  userProfiles: state.profile.userProfiles,
  userProfile: state.profile.userProfile,
  loading: state.profile.loading,
});

const mapDispatchToProps = {
  getUserProfiles,
  getUserProfile,
  updateUserProfile,
  deleteUserProfile,
};

export default connect(mapStateToProps, mapDispatchToProps)(UserProfilePage);
