import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import setAuthToken from "../../Utils/setAuthToken";

const initialState = {
  email: "",
  password: "",
  currentUser: null,
  error: null,
};

const LoginSlice = createSlice({
  name: "login",
  initialState,
  reducers: {
    setField: (state, action) => {
      const { field, value } = action.payload;
      state[field] = value;
    },
    loginSuccess: (state, action) => {
      state.error = null;
      const { currentUser } = action.payload;
      state.currentUser = currentUser;
    },
    loginFailure: (state, action) => {
      state.error = action.payload;
    },
    logout: (state) => {
      state.email = "";
      state.password = "";
      state.error = null;
    },
  },
});

export const { setField, loginSuccess, loginFailure, logout } =
  LoginSlice.actions;

  export const loadUser = () => async (dispatch) => {
    try {
      const token = localStorage.getItem("token");
      if (token) {
        setAuthToken(token);
      }
      const res = await axios.get("/api/auth");
      const currentUser = res.data; // Assuming currentUser is present in the response data
      dispatch(loginSuccess({ currentUser })); // Dispatch loginSuccess action with currentUser
      console.log("User loaded", currentUser);
    } catch (err) {
      console.log(err.response);
    }
  };
  

export const loginUser = (loginUser) => async (dispatch) => {
  try {
    const config = { headers: { "Content-Type": "application/json" } };
    const body = JSON.stringify(loginUser);
    console.log("111111111111111" ); 
    const res = await axios.post("/api/auth", body, config);
    console.log("222222222222222" ); 
    dispatch(loginSuccess());
    console.log("333333333333333" ); 
    localStorage.setItem("token", res.data.token);
    setAuthToken(res.data.token);
    dispatch(loadUser());
  } catch (err) {
    dispatch(loginFailure(err.response.data));
  }
};

export const logoutUser = () => async (dispatch) => {
  try {
    localStorage.removeItem("token");
    setAuthToken(null);
    dispatch(logout());
  } catch (err) {
    console.error(err);
  }
};

// ... other thunks ...
// You can add more thunks for updating profile, creating posts, etc.

export default LoginSlice.reducer;
