import React, { useEffect } from "react";
import { connect } from "react-redux";
import {
  getAdoptions,
  createAdoption,
  removeAdoption,
} from "../Redux/Reducers/AdoptionSlice";

const AdoptionCard = ({ adoption, onDelete }) => {
  return (
    <div>
      <h3>Adoption</h3>
      <p>{adoption.profile}</p>
      <p>{adoption.kitten}</p>
      <button onClick={() => onDelete(adoption._id)}>Remove Adoption</button>
    </div>
  );
};

const AdoptionList = ({ adoptions, onDelete }) => {
  return (
    <div>
      {adoptions.map((adoption) => (
        <AdoptionCard
          key={adoption._id}
          adoption={adoption}
          onDelete={onDelete}
        />
      ))}
    </div>
  );
};

const AdoptionPage = ({
  adoptions,
  loading,
  getAdoptions,
  createAdoption,
  removeAdoption,
}) => {
  useEffect(() => {
    getAdoptions();
  }, []);

  const handleCreateAdoption = (profileId, kittenId) => {
    createAdoption(profileId, kittenId);
  };

  const handleRemoveAdoption = (adoptionId) => {
    removeAdoption(adoptionId);
  };

  return (
    <div>
      <h1>Adoption Management</h1>
      <AdoptionList adoptions={adoptions} onDelete={handleRemoveAdoption} />
      {loading && <p>Loading...</p>}
      {/* Add a form to create adoptions */}
    </div>
  );
};

const mapStateToProps = (state) => ({
  adoptions: state.adoption.adoptions,
  loading: state.adoption.loading,
});

const mapDispatchToProps = {
  getAdoptions,
  createAdoption,
  removeAdoption,
};

export default connect(mapStateToProps, mapDispatchToProps)(AdoptionPage);
