import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { getKittens, addKitten, updateKitten, deleteKitten } from "../Redux/Reducers/ProfileSlice";

const KittenPage = ({ kittens, loading, getKittens, addKitten, updateKitten, deleteKitten }) => {
  useEffect(() => {
    getKittens();
  }, [kittens]);

  const [formData, setFormData] = useState({
    breed: "",
    alt: "",
    pic: "", // Add pic field if needed
  });

  const { breed, alt, pic } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  

  const handleAdd = () => {
    addKitten(formData);
    setFormData({
      breed: "",
      alt: "",
      pic: "",
    });
  };

  const handleUpdate = (kittenId) => {
    updateKitten(kittenId, formData);
    // You may need to reset the form fields here if needed
  };

  const handleDelete = (kittenId) => {
    deleteKitten(kittenId);
  };

  const [editKittenId, setEditKittenId] = useState(null); // Track the kitten being edited

const handleEdit = (kittenId) => {
  const editedKitten = kittens.find((kitten) => kitten._id === kittenId);
  setFormData({
    breed: editedKitten.breed,
    alt: editedKitten.alt,
    pic: "", // Add pic field if needed
  });
  setEditKittenId(kittenId);
};

  return (
    <div>
      <h1>Kitten Page</h1>
      {/* Add form for adding a new kitten */}
      <form>
        <input type="text" name="breed" value={breed} onChange={handleChange} />
        <input type="text" name="alt" value={alt} onChange={handleChange} />
        {/* Add input fields for other kitten information */}
        <button type="button" onClick={handleAdd}>Add Kitten</button>
      </form>

      {loading ? (
        <p>Loading kittens...</p>
      ) : (
        <ul>
  {kittens.map((kitten) => (
    <li key={kitten._id}>
      {editKittenId === kitten._id ? ( // Check if the kitten is being edited
        <div>
          <input
            type="text"
            name="breed"
            value={formData.breed}
            onChange={handleChange}
          />
          <input
            type="text"
            name="alt"
            value={formData.alt}
            onChange={handleChange}
          />
          <button onClick={() => handleUpdate(kitten._id)}>Save</button>
          <button onClick={() => setEditKittenId(null)}>Cancel</button>
        </div>
      ) : (
        <div>
          <h3>{kitten.breed}</h3>
          <p>{kitten.alt}</p>
          <button onClick={() => handleEdit(kitten._id)}>Edit</button>
          <button onClick={() => handleDelete(kitten._id)}>Delete</button>
        </div>
      )}
    </li>
  ))}
</ul>

      )}
    </div>
  );
};


const mapStateToProps = (state) => ({
  kittens: state.profile.kittens,
  loading: state.profile.loading,
});

const mapDispatchToProps = {
  getKittens,
  addKitten,
  updateKitten,
  deleteKitten,
};

export default connect(mapStateToProps, mapDispatchToProps)(KittenPage);
