import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {
  getAdoptions,
  createAdoptionRequest,
} from "../Redux/Reducers/AdoptionSlice";

const AdoptionCard = ({ adoption, onRequestAdoption }) => {
  const [showEmailInput, setShowEmailInput] = useState(false);
  const [emailContent, setEmailContent] = useState("");

  const handleToggleEmailInput = () => {
    setShowEmailInput(!showEmailInput);
    setEmailContent("");
  };

  const handleSendEmail = () => {
    onRequestAdoption(adoption._id, emailContent);
    setShowEmailInput(false);
    setEmailContent("");
  };

  return (
    <div>
      <h3>Adoption</h3>
      <p>{adoption.profile}</p>
      <p>{adoption.kitten}</p>
      <button onClick={handleToggleEmailInput}>
        {showEmailInput ? "Cancel" : "Apply for Adoption"}
      </button>
      {showEmailInput && (
        <div>
          <input
            type="text"
            value={emailContent}
            onChange={(e) => setEmailContent(e.target.value)}
            placeholder="Enter email content..."
          />
          <button onClick={handleSendEmail}>Submit</button>
        </div>
      )}
    </div>
  );
};

const AdoptionList = ({ adoptions, onRequestAdoption }) => {
  return (
    <div>
      {adoptions.map((adoption) => (
        <AdoptionCard
          key={adoption._id}
          adoption={adoption}
          onRequestAdoption={onRequestAdoption}
        />
      ))}
    </div>
  );
};

const AdoptionPage = ({
  adoptions,
  loading,
  getAdoptions,
  requestAdoption,
}) => {
  useEffect(() => {
    getAdoptions();
  }, []);

  const handleRequestAdoption = (adoptionId, emailContent) => {
    requestAdoption(adoptionId, emailContent);
  };

  return (
    <div>
      <h1>Adoption Management</h1>
      <AdoptionList
        adoptions={adoptions}
        onRequestAdoption={handleRequestAdoption}
      />
      {loading && <p>Loading...</p>}
    </div>
  );
};

const mapStateToProps = (state) => ({
  adoptions: state.adoption.adoptions,
  loading: state.adoption.loading,
});

const mapDispatchToProps = {
  getAdoptions,
  requestAdoption: createAdoptionRequest,
};

export default connect(mapStateToProps, mapDispatchToProps)(AdoptionPage);
