import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {
  getUserProfile,
  updateUserProfile,
  deleteUserProfile,
  searchUserProfiles,
} from "../Redux/Reducers/ProfileSlice";

const UserProfilePage = ({
  userProfile,
  currentUser,
  loading,
  getUserProfile,
  updateUserProfile,
  deleteUserProfile,
  searchUserProfiles,
}) => {
  useEffect(() => {
    getUserProfile(currentUser._id); // Get the current user's profile on initial load
  }, [getUserProfile, currentUser]);

  const [searchName, setSearchName] = useState("");
  const [editingProfile, setEditingProfile] = useState(false);

  const handleSearchChange = (e) => {
    setSearchName(e.target.value);
  };

  const handleSearchSubmit = () => {
    searchUserProfiles(searchName);
  };

  const handleEditProfile = () => {
    setEditingProfile(true);
  };

  const handleCancelEdit = () => {
    setEditingProfile(false);
  };

  const handleUpdateProfile = () => {
    updateUserProfile({
      _id: currentUser._id,
      name: userProfile.name, // Pass the current profile's name
      email: userProfile.email, // Pass the current profile's email
      // Add more fields as needed
    });
    setEditingProfile(false);
  };

  const handleDeleteProfile = () => {
    deleteUserProfile(currentUser._id);
    // Perform additional actions after profile deletion
  };

  return (
    <div>
      <h1>User Profile</h1>
      <p>Name: {userProfile.name}</p>
      <p>Email: {userProfile.email}</p>
      {/* Display other profile fields as needed */}
      
      <button onClick={handleEditProfile}>Edit Profile</button>
      <button onClick={handleDeleteProfile}>Delete Profile</button>

      {editingProfile && (
        <div>
          <h2>Edit Profile</h2>
          <form>
            <div>
              <label>Name:</label>
              <input type="text" name="name" value={userProfile.name} disabled />
            </div>
            <div>
              <label>Email:</label>
              <input type="email" name="email" value={userProfile.email} disabled />
            </div>
            {/* Add more input fields for other profile data */}
          </form>
          <button onClick={handleUpdateProfile}>Update Profile</button>
          <button onClick={handleCancelEdit}>Cancel</button>
        </div>
      )}

      <h1>Search User Profiles</h1>
      <input
        type="text"
        placeholder="Search by name..."
        value={searchName}
        onChange={handleSearchChange}
      />
      <button onClick={handleSearchSubmit}>Search</button>
      
      <ul>
        {/* Map over search results and display profiles */}
      </ul>

      {loading && <p>Loading...</p>}
    </div>
  );
};

const mapStateToProps = (state) => ({
  userProfile: state.profile.userProfile,
  currentUser: state.login.currentUser, // Assuming you store current user info in auth state
  loading: state.profile.loading,
});

const mapDispatchToProps = {
  getUserProfile,
  updateUserProfile,
  deleteUserProfile,
  searchUserProfiles,
};

export default connect(mapStateToProps, mapDispatchToProps)(UserProfilePage);
