// import React from 'react'

// export default function Shop() {
//   return (
//     <div>Shop</div>
//   )
// }

import React, { useState, useEffect } from "react";

const boardStyle = {
  backgroundColor: "#eee",
  width: "208px",
  alignItems: "center",
  justifyContent: "center",
  display: "flex",
  flexDirection: "column",
  border: "3px #eee solid",
};

const containerStyle = {
  display: "flex",
  alignItems: "center",
  flexDirection: "column",
};

const instructionsStyle = {
  marginTop: "5px",
  marginBottom: "5px",
  fontWeight: "bold",
  fontSize: "16px",
};

const buttonStyle = {
  marginTop: "15px",
  marginBottom: "16px",
  width: "80px",
  height: "40px",
  backgroundColor: "#8acaca",
  color: "white",
  fontSize: "16px",
};

function Square({ value, onClick }) {
  return (
    <div onClick={onClick} className="square" style={squareStyle}>
      {value}
    </div>
  );
}

const squareStyle = {
  width: "60px",
  height: "60px",
  backgroundColor: "#ddd",
  margin: "4px",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  fontSize: "20px",
  color: "white",
};

function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [xIsNext, setXIsNext] = useState(true);
  const [winner, setWinner] = useState(null);


  const renderSquare = (i) => {
    return <Square value={squares[i]} onClick={() => handleClick(i)} />;
  };

  const getStatus = () => {
    if (winner) {
      return `Winner: ${winner}`;
    } else {
      return `Next player: ${xIsNext ? "X" : "O"}`;
    }
  };

  const resetBoard = () => {
    setSquares(Array(9).fill(null));
    setXIsNext(true);
    setWinner(null);
  };

  // 在 Board 组件中的 handleClick 函数之前

const DELAY = 2000; // 2秒的思考时间延迟

// 在 useEffect 中添加一个新的状态，用于控制计算机是否在思考中
const [computerThinking, setComputerThinking] = useState(false);

// 修改 useEffect 部分为如下内容
useEffect(() => {
  if (!xIsNext && !winner && computerThinking) {
    const computerMoveIndex = calculateBestMove(squares, 'O');
    const newSquares = squares.slice();
    newSquares[computerMoveIndex] = 'O';
    setComputerThinking(false);
    setSquares(newSquares);

    if (calculateWinner(newSquares)) {
      setWinner('O');
    }

    setXIsNext(true);
  }
}, [xIsNext, winner, squares, computerThinking]);

// 添加一个新的函数，用于计算计算机的最佳移动
const calculateBestMove = (squares, player) => {
  const availableMoves = [];
  for (let i = 0; i < squares.length; i++) {
    if (!squares[i]) {
      availableMoves.push(i);
    }
  }

  let bestMove = null;
  let bestScore = player === 'X' ? -Infinity : Infinity;

  for (let i = 0; i < availableMoves.length; i++) {
    const move = availableMoves[i];
    const newSquares = squares.slice();
    newSquares[move] = player;

    const score = minimax(newSquares, 0, false);
    if ((player === 'X' && score > bestScore) || (player === 'O' && score < bestScore)) {
      bestScore = score;
      bestMove = move;
    }
  }

  return bestMove;
};

// 添加一个新的函数，用于实现 Minimax 算法
const minimax = (squares, depth, isMaximizing) => {
  const winner = calculateWinner(squares);
  if (winner === 'X') {
    return -10 + depth;
  } else if (winner === 'O') {
    return 10 - depth;
  } else if (squares.every((square) => square !== null)) {
    return 0;
  }

  if (isMaximizing) {
    let bestScore = -Infinity;
    for (let i = 0; i < squares.length; i++) {
      if (!squares[i]) {
        const newSquares = squares.slice();
        newSquares[i] = 'O';
        const score = minimax(newSquares, depth + 1, false);
        bestScore = Math.max(bestScore, score);
      }
    }
    return bestScore;
  } else {
    let bestScore = Infinity;
    for (let i = 0; i < squares.length; i++) {
      if (!squares[i]) {
        const newSquares = squares.slice();
        newSquares[i] = 'X';
        const score = minimax(newSquares, depth + 1, true);
        bestScore = Math.min(bestScore, score);
      }
    }
    return bestScore;
  }
};

// 修改 handleClick 函数，使计算机思考一段时间后再移动
const handleClick = (i) => {
  if (squares[i] || winner || (xIsNext && !squares.includes(null))) return;

  const newSquares = squares.slice();
  newSquares[i] = xIsNext ? 'X' : 'O';
  setSquares(newSquares);

  if (calculateWinner(newSquares)) {
    setWinner(newSquares[i]);
  }

  setXIsNext(!xIsNext);

  if (!xIsNext && !winner) {
    setComputerThinking(true);
    setTimeout(() => {
      setComputerThinking(false);
    }, DELAY);
  }
};

  return (
    <div style={containerStyle} className="gameBoard">
      <div id="statusArea" className="status" style={instructionsStyle}>
        {getStatus()}
      </div>
      <button style={buttonStyle} onClick={resetBoard}>
        Reset
      </button>
      <div style={boardStyle}>
        <div className="board-row" style={{ display: "flex" }}>
          {renderSquare(0)}
          {renderSquare(1)}
          {renderSquare(2)}
        </div>
        <div className="board-row" style={{ display: "flex" }}>
          {renderSquare(3)}
          {renderSquare(4)}
          {renderSquare(5)}
        </div>
        <div className="board-row" style={{ display: "flex" }}>
          {renderSquare(6)}
          {renderSquare(7)}
          {renderSquare(8)}
        </div>
      </div>
    </div>
  );
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

export default function Game() {
  return (
    <div className="game">
      <div className="game-board">
        <Board />
      </div>
    </div>
  );
}
