import React, { useEffect } from "react";
import { connect } from "react-redux";
import {
  getPosts,
  createPost,
  likePost,
  unlikePost,
  deletePost,
  getPost,
  addComment,
  removeComment,
} from "./postSlice";
import PostList from "./PostList"; // You need to create this component
import PostForm from "./PostForm"; // You need to create this component

const PostPage = ({
  posts,
  loading,
  getPosts,
  createPost,
  likePost,
  unlikePost,
  deletePost,
  getPost,
  addComment,
  removeComment,
}) => {
  useEffect(() => {
    getPosts();
  }, [getPosts]);

  const handleCreatePost = (formData) => {
    createPost(formData);
  };

  const handleLikePost = (postId) => {
    likePost(postId);
  };

  const handleUnlikePost = (postId) => {
    unlikePost(postId);
  };

  const handleDeletePost = (postId) => {
    deletePost(postId);
  };

  const handleGetPost = (postId) => {
    getPost(postId);
  };

  const handleAddComment = (postId, formData) => {
    addComment(postId, formData);
  };

  const handleRemoveComment = (postId, commentId) => {
    removeComment(postId, commentId);
  };

  return (
    <div>
      <h1>Post Management</h1>
      <PostForm onSubmit={handleCreatePost} />
      {loading ? (
        <p>Loading...</p>
      ) : (
        <PostList
          posts={posts}
          onLike={handleLikePost}
          onUnlike={handleUnlikePost}
          onDelete={handleDeletePost}
          onView={handleGetPost}
          onAddComment={handleAddComment}
          onRemoveComment={handleRemoveComment}
        />
      )}
    </div>
  );
};

const mapStateToProps = (state) => ({
  posts: state.post.posts,
  loading: state.post.loading,
});

const mapDispatchToProps = {
  getPosts,
  createPost,
  likePost,
  unlikePost,
  deletePost,
  getPost,
  addComment,
  removeComment,
};

export default connect(mapStateToProps, mapDispatchToProps)(PostPage);
