import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {
  getUserProfile,
  getUserProfiles,
  updateUserProfile,
  deleteUserProfile,
} from "../Redux/Reducers/ProfileSlice";

const UserProfilePage = ({
  userProfiles,
  userProfile,
  loading,
  getUserProfile,
  getUserProfiles,
  updateUserProfile,
  deleteUserProfile,
}) => {
  useEffect(() => {
    getUserProfiles();
  }, []);

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    // Add more fields as needed
  });

  const { name, email } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleViewProfile = (userId) => {
    getUserProfile(userId);
  };

  const handleUpdateProfile = () => {
    updateUserProfile(formData);
    // You may want to reset form fields after update
  };

  const handleDeleteProfile = () => {
    deleteUserProfile();
    // Perform any additional actions after profile deletion
  };

  return (
    <div>
      <h1>User Profiles</h1>
      <ul>
        {userProfiles.map((user) => (
          <li key={user._id}>
            <p>{user.name}</p>
            <button onClick={() => handleViewProfile(user._id)}>View Profile</button>
          </li>
        ))}
      </ul>

      {userProfile && (
        <div>
          <h2>View Profile</h2>
          <form>
            <div>
              <label>Name:</label>
              <input type="text" name="name" value={name} onChange={handleChange} />
            </div>
            <div>
              <label>Email:</label>
              <input type="email" name="email" value={email} onChange={handleChange} />
            </div>
            {/* Add more input fields for other profile data */}
          </form>
          <button onClick={handleUpdateProfile}>Update Profile</button>
          <button onClick={handleDeleteProfile}>Delete Profile</button>
        </div>
      )}

      {loading && <p>Loading user profiles...</p>}
    </div>
  );
};

const mapStateToProps = (state) => ({
  userProfiles: state.userProfile.userProfiles,
  userProfile: state.userProfile.userProfile,
  loading: state.userProfile.loading,
});

const mapDispatchToProps = {
  getUserProfile,
  getUserProfiles,
  updateUserProfile,
  deleteUserProfile,
};

export default connect(mapStateToProps, mapDispatchToProps)(UserProfilePage);
