import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {
  createAdoption, // Add your Redux action for creating an adoption
  getAdoptions,   // Add your Redux action for getting adoptions
  deleteAdoption, // Add your Redux action for deleting an adoption
} from "../Redux/Reducers/AdoptionSlice"; // Adjust the import path

const AdoptionPage = ({
  adoptions,
  loading,
  createAdoption,
  getAdoptions,
  deleteAdoption,
  currentUser,
}) => {
  const [selectedKitten, setSelectedKitten] = useState("");
  
  useEffect(() => {
    getAdoptions();
  }, [adoptions]);

  const handleCreateAdoption = () => {
    if (selectedKitten) {
      createAdoption({ kitten: selectedKitten });
      setSelectedKitten("");
    }
  };

  const handleDeleteAdoption = (adoptionId) => {
    deleteAdoption(adoptionId);
  };

  return (
    <div>
      <h1>Adoption Management</h1>
      <select
        value={selectedKitten}
        onChange={(e) => setSelectedKitten(e.target.value)}
      >
        <option value="">Select a kitten...</option>
        {/* Map over your kittens and populate the options */}
      </select>
      <button onClick={handleCreateAdoption}>Adopt Kitten</button>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {adoptions.map((adoption) => (
            <div key={adoption._id}>
              <p>Kitten: {adoption.kitten.breed}</p>
              <button onClick={() => handleDeleteAdoption(adoption._id)}>
                Cancel Adoption
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const mapStateToProps = (state) => ({
  adoptions: state.adoption.adoptions, // Adjust the state property name
  loading: state.adoption.loading,     // Adjust the state property name
  currentUser: state.login.currentUser,
});

const mapDispatchToProps = {
  createAdoption,
  getAdoptions,
  deleteAdoption,
};

export default connect(mapStateToProps, mapDispatchToProps)(AdoptionPage);
