import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {
  getAdoptions, // Replace with the actual Redux action for getting adoptions
  createAdoption, // Replace with the actual Redux action for creating an adoption
  deleteAdoption, // Replace with the actual Redux action for deleting an adoption
} from "../Redux/Reducers/AdoptionSlice";

const AdoptionCard = ({
  adoption,
  currentUser,
  onDelete,
}) => {
  return (
    <div>
      <h3>Adoption</h3>
      <p>Kitten: {adoption.kitten.breed}</p>
      {currentUser && (
        <button onClick={() => onDelete(adoption._id)}>Delete Adoption</button>
      )}
    </div>
  );
};

const AdoptionList = ({ adoptions, onDelete, currentUser }) => {
  return (
    <div>
      {adoptions.map((adoption) => (
        <AdoptionCard
          key={adoption._id}
          adoption={adoption}
          onDelete={onDelete}
          currentUser={currentUser}
        />
      ))}
    </div>
  );
};

const AdoptionForm = ({ onSubmit }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(); // Call the Redux action to create an adoption
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Create New Adoption</h3>
      <button type="submit">Create Adoption</button>
    </form>
  );
};

const AdoptionPage = ({
  adoptions,
  loading,
  currentUser,
  getAdoptions,
  createAdoption,
  deleteAdoption,
}) => {
  useEffect(() => {
    getAdoptions(); // Call the Redux action to get adoptions
  }, []);

  const handleCreateAdoption = () => {
    createAdoption(); // Call the Redux action to create an adoption
  };

  const handleDeleteAdoption = (adoptionId) => {
    deleteAdoption(adoptionId); // Call the Redux action to delete an adoption
  };

  return (
    <div>
      <h1>Adoption Management</h1>
      <AdoptionForm onSubmit={handleCreateAdoption} />
      {loading ? (
        <p>Loading...</p>
      ) : (
        <AdoptionList
          adoptions={adoptions}
          onDelete={handleDeleteAdoption}
          currentUser={currentUser}
        />
      )}
    </div>
  );
};

const mapStateToProps = (state) => ({
  adoptions: state.adoption.adoptions, // Replace with the actual Redux state property
  loading: state.adoption.loading, // Replace with the actual Redux state property
  currentUser: state.login.currentUser,
});

const mapDispatchToProps = {
  getAdoptions,
  createAdoption,
  deleteAdoption,
};

export default connect(mapStateToProps, mapDispatchToProps)(AdoptionPage);
