import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import {
  getKittens,
  addKitten,
  updateKitten,
  deleteKitten,
} from "../Redux/Reducers/ProfileSlice";

const KittenPage = ({
  kittens,
  loading,
  getKittens,
  addKitten,
  updateKitten,
  deleteKitten,
}) => {
  useEffect(() => {
    getKittens();
  }, [kittens]);

  const [formData, setFormData] = useState({
    breed: "",
    alt: "",
    pic: "", // Add pic field if needed
  });

  const { breed, alt, pic } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAdd = () => {
    addKitten(formData);
    setFormData({
      breed: "",
      alt: "",
      pic: "",
    });
  };

  const handleDelete = (kittenId) => {
    deleteKitten(kittenId);
  };

  const [editKittenId, setEditKittenId] = useState(null); // Track the kitten being edited
  const [editFormData, setEditFormData] = useState({
    breed: "",
    alt: "",
    pic: "", // Add pic field if needed
  });

  const handleEdit = (kittenId) => {
    const editedKitten = kittens.find((kitten) => kitten._id === kittenId);
    setEditFormData({
      breed: editedKitten.breed,
      alt: editedKitten.alt,
      pic: "", // Add pic field if needed
    });
    setEditKittenId(kittenId);
  };

  const handleEditChange = (e) => {
    setEditFormData({ ...editFormData, [e.target.name]: e.target.value });
  };
  
  const handleCancelEdit = (kittenId) => {
    setEditKittenId(null);
    setEditFormData({
      breed: "",
      alt: "",
      pic: "",
    });
  };
  
  const handleUpdate = (kittenId) => {
    updateKitten(kittenId, formData);
    setEditKittenId(null);
    setFormData({
      breed: "",
      alt: "",
      pic: "",
    });
  };

  return (
    <div>
      <h1>Kitten Page</h1>
      <ul>
        {kittens.map((kitten) => (
          <li key={kitten._id}>
            {editKittenId === kitten._id ? (
              <div>
                <input
                  type="text"
                  name="breed"
                  value={editFormData.breed}
                  onChange={handleEditChange}
                />
                <input
                  type="text"
                  name="alt"
                  value={editFormData.alt}
                  onChange={handleEditChange}
                />
                <button onClick={() => handleUpdate(kitten._id)}>Save</button>
                <button onClick={() => handleCancelEdit(kitten._id)}>Cancel</button>
              </div>
            ) : (
              <div>
                <h3>{kitten.breed}</h3>
                <p>{kitten.alt}</p>
                <button onClick={() => handleEdit(kitten._id)}>Edit</button>
                <button onClick={() => handleDelete(kitten._id)}>Delete</button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

const mapStateToProps = (state) => ({
  kittens: state.profile.kittens,
  loading: state.profile.loading,
});

const mapDispatchToProps = {
  getKittens,
  addKitten,
  updateKitten,
  deleteKitten,
};

export default connect(mapStateToProps, mapDispatchToProps)(KittenPage);
